using Cosmos.System.Graphics;
using System;
using System.IO;

namespace PrismProject
{
    class Driver
    {
        public static int screenX = 800;
        public static int screenY = 600;
        public static ColorDepth colors = ColorDepth.ColorDepth32;
        //ive added diferent graphics methods for testing
        //public static Canvas canvas = FullScreenCanvas.GetFullScreenCanvas(); - tested, very slow
        public static SVGAIICanvas canvas = new SVGAIICanvas(new Mode(screenX, screenY, colors));
        //public static VGACanvas canvas = new VGACanvas(); EXTREMLY slow, and it is super buggy
        //public static VBECanvas canvas = new VBECanvas(); doesnt work

        public static void Init()
        {
            canvas.Mode = new Mode(screenX, screenY, colors);
            string charset = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890!@#$%^&*();:\"\'\\/\\-_=+<>,.`~[]{}|/*-+";
            MemoryStream Lucida_Sans = new MemoryStream(Convert.FromBase64String(""));
            MemoryStream Century_Gothic = new MemoryStream(Convert.FromBase64String(""));
            MemoryStream Arial_Rounded_MT = new MemoryStream(Convert.FromBase64String(""));
            BitFont.RegisterBitFont("Lucida Sans", new BitFontDescriptor(charset, Lucida_Sans, 32));
            BitFont.RegisterBitFont("Century Gothic", new BitFontDescriptor(charset, Century_Gothic, 32));
            BitFont.RegisterBitFont("Arial Rounded MT", new BitFontDescriptor(charset, Arial_Rounded_MT, 32));
        }
    }
}
