using Cosmos.System.Graphics;
using System;
using System.IO;

namespace PrismProject
{
    class Driver
    {
        public static int screenX = 800;
        public static int screenY = 600;
        public static ColorDepth colors = ColorDepth.ColorDepth32;
        //public static Canvas canvas = FullScreenCanvas.GetFullScreenCanvas(); - tested, very slow
        public static SVGAIICanvas canvas = new SVGAIICanvas(new Mode(screenX, screenY, colors));

        public static void Init()
        {
            canvas.Mode = new Mode(screenX, screenY, colors);
            string charset = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890!@#$%^&*();:\"\'\\/\\-_=+<>,.`~[]{}|/*-+";
            MemoryStream MicrosoftSansSerifCustomCharset16 = new MemoryStream(Convert.FromBase64String(""));
            BitFont.RegisterBitFont("Microsoft_Sans_Serif", new BitFontDescriptor(charset, MicrosoftSansSerifCustomCharset16, 16));
            string CustomCharset = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890!@#$%^&*();:\"\'\\/-_=+<>,.`~[]{}|/*-+";
MemoryStream CenturyGothicCustomCharset16 = new MemoryStream(Convert.FromBase64String(""));
            BitFont.RegisterBitFont("Century_Gothic", new BitFontDescriptor(CustomCharset, CenturyGothicCustomCharset16, 16));
        }
    }
}
