using Cosmos.System.Graphics;
using System;
using System.IO;

namespace PrismProject.System2.Drivers
{
    internal class Video
    {
            public static int SW = 1280, SH = 720;
            public static SVGAIICanvas Screen = new SVGAIICanvas(new Mode(SW, SH, ColorDepth.ColorDepth32));
            public static string Font = "YuGothicUI";

            public static void Init()
            {
                string CustomCharset = "🡬abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ1234567890!@#$%^&*()~`\"\':;?/>.<,{[}]\\|+=_-";
                MemoryStream YuGothicUICustomCharset16 = new MemoryStream(Convert.FromBase64String(""));
                External.BitFont.RegisterBitFont("YuGothicUI", new External.BitFont.BitFontDescriptor(CustomCharset, YuGothicUICustomCharset16, 16));
            }
    }
}
